import React from "react";
import "./App.css";
import { Container, FormGroup, Form, Input, Label, Button } from "reactstrap";
import axios from "axios";

function App() {
  this.state = {
    name: "",
    message: "",
  };

  const handleSubmit = (e) => {
    debugger;
    console.log(e);
    axios.post("http://localhost:5000/messages", { bad: "life choices" });
  };

  return (
    <div className="App">
      <Container className="themed-container border mt-5">
        <h1>Send Message</h1>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input
              type="text"
              placeholder="Insert screen name here."
              value={this.state.name}
              onChange={(e) => this.setState({ name: e.target.value })}
            ></Input>
          </FormGroup>
          <FormGroup>
            <Label for="input message">Message</Label>
            <Input
              type="textarea"
              placeholder="Write your message here."
              value={this.state.message}
              onChange={(e) => this.setState({ message: e.target.value })}
            ></Input>
          </FormGroup>
          <Button type="submit">Send Message</Button>
        </Form>
      </Container>
    </div>
  );
}

export default App;
