  };

  return (
    <div className="App">
      <Container className="themed-container border mt-5">
        <h1>Send Message</h1>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label for="name">Name</Label>
            <Input type="text" placeholder="Insert screen name here."></Input>
          </FormGroup>
          <FormGroup>
            <Label for="input message">Message</Label>
            <Input
              type="textarea"
              placeholder="Write your message here."
            ></Input>
          </FormGroup>
        </Form>
        <Button type="submit">Send Message</Button>
      </Container>
    </div>
  );
}

export default App;
