import React from "react";
import "./App.css";
import { Container, FormGroup, Form, Input, Label, Button } from "reactstrap";
import axios from "axios";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      message: "",
      messages: [],
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    this.getMessages();
  }

  getMessages() {
    axios
      .get("http://localhost:5000/messages")
      .then((res) => this.setState({ messages: res }))
      .catch((err) => console.log(err));
  }

  handleSubmit(e) {
    e.preventDefault();
    axios.post("http://localhost:5000/messages", this.state);
  }

  render() {
    return (
      <div className="App">
        <Container className="themed-container border mt-5">
          <h1>Send Message</h1>
          {JSON.stringify(this.state.messages)}
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input
                type="text"
                placeholder="Insert screen name here."
                value={this.state.name}
                onChange={(e) => this.setState({ name: e.target.value })}
              ></Input>
            </FormGroup>
            <FormGroup>
              <Label for="input message">Message</Label>
              <Input
                type="textarea"
                placeholder="Write your message here."
                value={this.state.message}
                onChange={(e) => this.setState({ message: e.target.value })}
              ></Input>
            </FormGroup>
            <Button type="submit">Send Message</Button>
          </Form>
        </Container>
      </div>
    );
  }
}

export default App;
