import React from "react";
import "./App.css";
import { Container, FormGroup, Form, Input, Label, Button } from "reactstrap";
import axios from "axios";
import openSocket from "socket.io-client";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      message: "",
      messages: [],
    };

    this.handleSubmit = this.handleSubmit.bind(this);

    this.socket = openSocket("http://localhost:5000/messages");
    // thils.socket.on("message", () => console.log("socket fired"));
    socket.on("hello", (message) => console.log(message));
  }

  componentDidMount() {
    this.getMessages();
    console.log("mounted");
  }

  getMessages() {
    axios
      .get("http://localhost:5000/messages")
      .then((res) =>
        this.setState({
          messages: res.data.map((message) => (
            <div>
              <h4>{message.name}</h4>
              <p>{message.message}</p>
            </div>
          )),
        })
      )
      .catch((err) => console.log(err));
  }

  handleSubmit(e) {
    e.preventDefault();
    axios.post("http://localhost:5000/messages", this.state);
    this.setState({ message: "" });
  }

  render() {
    return (
      <div className="App">
        <Container className="themed-container border mt-5 shadow p-3 rounded ">
          <div className="messages">{this.state.messages}</div>
          <Form onSubmit={this.handleSubmit}>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input
                type="text"
                placeholder="Insert screen name here."
                value={this.state.name}
                onChange={(e) => this.setState({ name: e.target.value })}
              ></Input>
            </FormGroup>
            <FormGroup>
              <Label for="input message">Message</Label>
              <Input
                type="textarea"
                placeholder="Write your message here."
                value={this.state.message}
                onChange={(e) => this.setState({ message: e.target.value })}
              ></Input>
            </FormGroup>
            <Button type="submit">Send Message</Button>
          </Form>
        </Container>
      </div>
    );
  }
}

export default App;
